@count = 0
def find_substring(s, words)
  tally = words.tally
  word_length = words.first.length
  valid_length = words.length * word_length
  return [] if valid_length > s.length
  valid_array = []

  for i in 0..s.length - valid_length
    if valid_substring?(s[i..i + valid_length - 1], tally.dup, word_length, valid_length)
      valid_array << i
    end
  end

  puts @count
  valid_array
end

def valid_substring?(s, t, word_length, valid_length)
  i = 0
  while i < s.length do
    word = s[i,word_length]
    @count += 1
    return false if t[word].nil? || t[word] <= 0

    t[word] -= 1
    i += word_length
  end

  true
end


